import React, { useState, useEffect } from 'react';
import { questions } from './data/questions';
import { technologies } from './data/technologies';
import { analyzeAnswers } from './utils/analysis';
import ExportProgram from './components/ExportProgram';
import './index.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function BusinessTechAdvisor() {
  const [currentStep, setCurrentStep] = useState(0);
  const [answers, setAnswers] = useState({});
  const [analysisResults, setAnalysisResults] = useState(null);
  const [textInput, setTextInput] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analyzeError, setAnalyzeError] = useState(null);

  useEffect(() => {
    // Load saved answers from localStorage
    const saved = localStorage.getItem('techAdvisorAnswers');
    if (saved) {
      const parsed = JSON.parse(saved);
      setAnswers(parsed);
    }
  }, []);

  useEffect(() => {
    // Save answers to localStorage
    localStorage.setItem('techAdvisorAnswers', JSON.stringify(answers));
  }, [answers]);

  const handleSelectAnswer = (questionId, value) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: value
    }));
  };

  const handleTextChange = (e) => {
    setTextInput(e.target.value);
  };

  const handleNext = async () => {
    const q = questions[currentStep];

    if (q.type === 'textarea') {
      setAnswers(prev => ({
        ...prev,
        [q.id]: textInput
      }));
    }

    if (currentStep === questions.length - 1) {
      // Get all answers including current textarea
      const allAnswers = q.type === 'textarea'
        ? { ...answers, [q.id]: textInput }
        : answers;

      // Try to use backend API for AI-powered analysis
      await performAIAnalysis(allAnswers);
    } else {
      setCurrentStep(currentStep + 1);
    }
  };

  const performAIAnalysis = async (allAnswers) => {
    setIsAnalyzing(true);
    setAnalyzeError(null);

    try {
      // Prepare user analysis data
      const userAnalysis = {
        businessName: allAnswers.businessName || 'Your Business',
        industry: allAnswers.industry,
        size: allAnswers.size,
        budget: allAnswers.budget,
        primaryChallenges: allAnswers.challenges,
        goals: allAnswers.goals,
        timeline: allAnswers.timeline,
        additionalContext: allAnswers.additionalContext || ''
      };

      // Call backend API for OpenAI-powered analysis
      const response = await fetch(`${API_URL}/export/program`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          userAnalysis,
          recommendations: [], // Backend will generate these
          generateAnalysis: true // Signal that we want AI analysis
        })
      });

      if (!response.ok) {
        throw new Error('Failed to generate AI analysis');
      }

      // For now, use local analysis as fallback and display it
      const localResults = analyzeAnswers(allAnswers);
      setAnalysisResults({
        ...localResults,
        userAnalysis,
        hasAIAnalysis: true,
        apiAvailable: true
      });
    } catch (err) {
      console.error('AI Analysis error:', err);
      // Fallback to local analysis if API fails
      const localResults = analyzeAnswers(allAnswers);
      setAnalysisResults({
        ...localResults,
        userAnalysis: {
          businessName: allAnswers.businessName || 'Your Business',
          industry: allAnswers.industry,
          size: allAnswers.size,
          budget: allAnswers.budget,
          primaryChallenges: allAnswers.challenges,
          goals: allAnswers.goals,
          timeline: allAnswers.timeline
        },
        apiAvailable: false
      });
      setAnalyzeError('Backend API unavailable - showing local recommendations');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleReset = () => {
    setCurrentStep(0);
    setAnswers({});
    setAnalysisResults(null);
    setTextInput('');
    setAnalyzeError(null);
    localStorage.removeItem('techAdvisorAnswers');
  };

  if (analysisResults) {
    return <ResultsView results={analysisResults} onReset={handleReset} />;
  }

  const q = questions[currentStep];
  const progress = ((currentStep + 1) / questions.length) * 100;
  const isAnswered = answers[q.id];

  return (
    <div className="advisor-container">
      <div className="bg-container">
        <svg viewBox="0 0 1200 800" preserveAspectRatio="xMidYMid slice">
          {/* Background SVG patterns - same as original */}
          <defs>
            <linearGradient id="grad1" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" style={{ stopColor: '#1e3a5f', stopOpacity: 0.05 }} />
              <stop offset="100%" style={{ stopColor: '#00d4ff', stopOpacity: 0.05 }} />
            </linearGradient>
          </defs>
          <rect width="1200" height="800" fill="url(#grad1)" />
          <g stroke="#00d4ff" strokeWidth="1.5" fill="none" opacity="0.15">
            <path d="M 100,100 L 150,70 L 200,100 L 200,160 L 150,190 L 100,160 Z"/>
            <path d="M 220,150 L 270,120 L 320,150 L 320,210 L 270,240 L 220,210 Z"/>
          </g>
        </svg>
      </div>

      <div className="container">
        <div className="header">
          <h1 className="logo">Business Tech Advisor</h1>
          <p className="tagline">‚ú® ◊û◊ó◊§◊© ◊ê◊™ ◊î◊õ◊ú◊ô ◊î◊†◊õ◊ï◊ü ◊ú◊¢◊°◊ß◊ö? ◊™◊ü ◊ú◊†◊ï ◊ú◊¢◊ñ◊ï◊® ◊ú◊ö!</p>
        </div>

        <div className="card fade-in-up">
          <div className="progress-section">
            <div className="progress-text">◊©◊ú◊ë {currentStep + 1} ◊û◊™◊ï◊ö {questions.length}</div>
            <div className="progress-bar">
              <div className="progress-fill" style={{ width: `${progress}%` }}></div>
            </div>
          </div>

          <h2>{q.text}</h2>
          <p className="subtitle">{q.subtitle}</p>

          <div className="options-section">
            {q.type === 'multiple' && (
              <div className="options">
                {q.options.map(opt => (
                  <div
                    key={opt.value}
                    className={`option ${answers[q.id] === opt.value ? 'selected' : ''}`}
                    onClick={() => handleSelectAnswer(q.id, opt.value)}
                  >
                    <div className="option-title">{opt.label}</div>
                    <div className="option-desc">{opt.desc}</div>
                  </div>
                ))}
              </div>
            )}

            {q.type === 'textarea' && (
              <div className="textarea-section">
                <textarea
                  className="textarea-input"
                  placeholder={q.placeholder}
                  value={textInput}
                  onChange={handleTextChange}
                />
              </div>
            )}
          </div>

          <div className="button-group">
            {currentStep > 0 && (
              <button className="btn-secondary" onClick={handlePrevious} disabled={isAnalyzing}>
                ‚Üê ◊ó◊ñ◊ï◊®
              </button>
            )}
            <button
              className="btn-primary"
              onClick={handleNext}
              disabled={(!isAnswered && q.type === 'multiple') || isAnalyzing}
            >
              {isAnalyzing ? (
                <>
                  <span style={{ marginRight: 8 }}>üîÑ</span>
                  ◊û◊†◊™◊ó...
                </>
              ) : currentStep === questions.length - 1 ? '◊ß◊ë◊ú ◊î◊û◊ú◊¶◊ï◊™ ‚ûú' : '◊î◊ë◊ê ‚ûú'}
            </button>
          </div>
          {analyzeError && (
            <div style={{ marginTop: 16, padding: 12, backgroundColor: '#fff3cd', borderRadius: 8, color: '#856404' }}>
              ‚ö†Ô∏è {analyzeError}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

function ResultsView({ results, onReset }) {
  // Extract recommendations that are objects with proper structure
  const recommendations = Array.isArray(results) && results.length > 0 && typeof results[0] === 'object'
    ? results
    : [];

  // Extract user analysis if it exists in results
  const userAnalysis = results.userAnalysis || {};

  return (
    <div className="advisor-container">
      <div className="bg-container">
        <svg viewBox="0 0 1200 800" preserveAspectRatio="xMidYMid slice">
          <defs>
            <linearGradient id="grad1" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" style={{ stopColor: '#1e3a5f', stopOpacity: 0.05 }} />
              <stop offset="100%" style={{ stopColor: '#00d4ff', stopOpacity: 0.05 }} />
            </linearGradient>
          </defs>
          <rect width="1200" height="800" fill="url(#grad1)" />
        </svg>
      </div>

      <div className="container">
        <div className="results-header fade-in-up">
          <h1>üéØ ◊î◊û◊ú◊¶◊ï◊™ ◊ò◊õ◊†◊ï◊ú◊ï◊í◊ô◊ï◊™ ◊¢◊ë◊ï◊®◊ö</h1>
          <p className="subtitle">◊ë◊î◊™◊ê◊ù ◊ú◊™◊©◊ï◊ë◊ï◊™◊ô◊ö, ◊î◊†◊î 4 ◊î◊§◊™◊®◊ï◊†◊ï◊™ ◊î◊ò◊ï◊ë◊ô◊ù ◊ë◊ô◊ï◊™◊® ◊ú◊î◊¢◊ú◊ê◊™ ◊î◊¢◊°◊ß ◊©◊ú◊ö</p>
          {results.apiAvailable === false && (
            <p style={{ color: '#ff9800', fontSize: 14, marginTop: 8 }}>
              üí° ◊î◊û◊ú◊¶◊ï◊™ ◊ê◊ú◊ï ◊î◊ù ◊ë◊î◊™◊ê◊ù ◊ú◊†◊ô◊™◊ï◊ó ◊û◊ß◊ï◊û◊ô. ◊ú◊ß◊ë◊ú◊™ ◊†◊ô◊™◊ï◊ó ◊û◊§◊ï◊®◊ò ◊¢◊ù OpenAI, ◊†◊ê ◊ú◊ë◊ì◊ï◊ß ◊ê◊™ ◊ó◊ô◊ë◊ï◊® ◊î◊©◊®◊™.
            </p>
          )}
        </div>

        <div className="results-grid">
          {recommendations.map((rec, idx) => (
            <div key={idx} className="recommendation-card fade-in-up">
              <div className="rec-header">
                <div>
                  <div className="rec-title">{rec.name}</div>
                  <div className="rec-category">{rec.category}</div>
                </div>
                <div className="priority-badge">{rec.priority}</div>
              </div>

              <p className="rec-description">{rec.description}</p>

              <ul className="factors">
                {rec.factors.map((f, i) => (
                  <li key={i}>{f}</li>
                ))}
              </ul>

              <div className="step-list">
                <div style={{ fontWeight: 600, color: 'var(--primary)', marginBottom: 12 }}>
                  üìã ◊™◊ï◊õ◊†◊ô◊™ ◊ô◊ô◊©◊ï◊ù:
                </div>
                {rec.steps.map((step, i) => (
                  <div key={i} className="step-item">
                    <div className="step-number">{i + 1}</div>
                    <div className="step-content">
                      <p>{step}</p>
                    </div>
                  </div>
                ))}
              </div>

              <div className="rec-meta">
                <div className="meta-item">
                  <span>üí∞</span>
                  <span>{rec.pricing}</span>
                </div>
                <div className="meta-item">
                  <span>‚è±Ô∏è</span>
                  <span>{rec.setup}</span>
                </div>
                <div className="meta-item" style={{ gridColumn: '1 / -1' }}>
                  <span>üìä</span>
                  <span>◊û◊ï◊®◊õ◊ë◊ï◊™: {rec.complexity}</span>
                </div>
              </div>

              <a href={rec.link} target="_blank" rel="noopener noreferrer" className="rec-link">
                ◊ë◊ß◊® ◊ë◊ê◊™◊® ‚ûú
              </a>
            </div>
          ))}
        </div>

        {/* Export Program Component */}
        {Object.keys(userAnalysis).length > 0 && recommendations.length > 0 && (
          <div style={{ margin: '60px 0', padding: '40px 0', borderTop: '1px solid #eee' }}>
            <ExportProgram userAnalysis={userAnalysis} recommendations={recommendations} />
          </div>
        )}

        <div style={{ textAlign: 'center', margin: '40px 0' }}>
          <button className="btn-primary" onClick={onReset} style={{ fontSize: 16, padding: '14px 32px' }}>
            üîÑ ◊™◊ü ◊ú◊ô ◊ú◊†◊°◊ï◊™ ◊©◊ï◊ë
          </button>
        </div>
      </div>
    </div>
  );
}

export default BusinessTechAdvisor;
